mapper.select:
		
			SELECT	faq_bno, admin_id, faq_title, faq_content, faq_regdate
 			FROM	( SELECT /*+INDEX_DESC(faq pk_board)*/
 					  rownum rn, faq_bno, admin_id, faq_title, faq_content, faq_regdate 
 					  FROM faq
 					  WHERE	
 		

		
 			
 		
 			rownum <= #{pageNum} * #{amount}
 			)
 		WHERE rn > (#{pageNum} -1) * #{amount}
