mapper.select:
		
			SELECT	noticeNo, adminId, noticeTitle, noticeContent, noticeRegdate
 			FROM	( SELECT /*+INDEX_DESC(notice notice_pk)*/
 					  rownum rn, noticeNno, adminId, noticeTitle, noticeContent, noticeRegdate 
 					  FROM notice
 					  WHERE	
 		

		
 			
 		
 			rownum <= #{pageNum} * #{amount}
 			)
 		WHERE rn > (#{pageNum} -1) * #{amount}

mapper.update:
UPDATE faq
SET noticeTitle = #{noticeTitle}, faqContent = #{faqContent}, faqRegdate = sysdate
WHERE noticeNo = #{noticeNo}
