mapper.select:
		
			SELECT	notice_bno, admin_id, notice_title, notice_content, notice_regdate
 			FROM	( SELECT /*+INDEX_DESC(notice notice_pk)*/
 					  rownum rn, notice_bno, admin_id, notice_title, notice_content, notice_regdate 
 					  FROM notice
 					  WHERE	
 		

		
 			
 		
 			rownum <= #{pageNum} * #{amount}
 			)
 		WHERE rn > (#{pageNum} -1) * #{amount}
